package com.fr.bi.cal.analyze.cal.sssecret;import java.util.HashMap;import java.util.Map;import com.fr.bi.cal.analyze.cal.result.Node;import com.fr.bi.stable.report.result.DimensionCalculator;import com.fr.bi.stable.report.result.LightNode;import com.fr.bi.stable.report.result.TargetCalculator;/** * Created by Hiram on 2015/1/28. */public class TreeNoneDimensionGroup extends NoneDimensionGroup {    private ISingleDimensionGroup cache;    private LightNode lightNode;    public TreeNoneDimensionGroup(LightNode lightNode) {        this.lightNode = lightNode;    }    protected void initRoot() {        node = new Node(null, lightNode.getData());        Map<TargetCalculator, Object> map = extractMap();        node.setTargetIndexValueMap(lightNode.getTargetIndexValueMap());        node.setGroupValueIndexMap(lightNode.getGroupValueIndexMap());        node.setSummaryValueMap(map);    }    @Override    public Node getRoot() {        throw new UnsupportedOperationException();    }    public void setRoot(LightNode root) {        this.lightNode = root;    }    @Override    public LightNode getLightNode() {        return lightNode;    }    public LightNode getTargetGettingKeyRoot() {        return lightNode;    }    private Map<TargetCalculator, Object> extractMap() {        Map summaryValue = lightNode.getSummaryValueMap();        if (summaryValue == null) {            return null;        }        Map<TargetCalculator, Object> map = new HashMap<TargetCalculator, Object>();        for (Object key : summaryValue.keySet()) {            if (key instanceof TargetCalculator) {                map.put((TargetCalculator) key, summaryValue.get(key));            }        }        return map;    }    @Override    public ISingleDimensionGroup createSingleDimensionGroup(DimensionCalculator[] pck, int[] pckindex, DimensionCalculator ck, Object[] data, int ckIndex, boolean useRealData) {        return getSingleDimensionGroup();    }    public ISingleDimensionGroup getSingleDimensionGroup() {        if (cache == null) {            cache = new TreeSingleDimensionGroup(lightNode);        }        return cache;    }    @Override    public Number getSummaryValue(TargetCalculator key) {        return lightNode.getSummaryValue(key);    }}