package com.fr.swift.util;import java.util.Collection;import java.util.Map;/** * Created by Hiram on 2015/9/17. */public class TypeUtils {    /**     * target是否可以赋值给maySuper参数。     *     * @param maySuper 变量的类型     * @param target   实际类型     * @return     */    public static Boolean isAssignable(Class maySuper, Class target) {        return maySuper.isAssignableFrom(target);    }    public static Boolean isIterableObject(Object value) {        return value instanceof Iterable;    }    public static Boolean isMapObject(Object value) {        return value instanceof Map;    }    public static Boolean isArrayObject(Object value) {        return value.getClass().isArray();    }    public static Boolean isIterableType(Class fieldClass) {        return TypeUtils.isAssignable(Iterable.class, fieldClass);    }    public static Boolean isCollectionType(Class fieldClass) {        return TypeUtils.isAssignable(Collection.class, fieldClass);    }    public static Boolean isMapType(Class fieldClass) {        return TypeUtils.isAssignable(Map.class, fieldClass);    }    public static Boolean isArrayType(Class fieldClass) {        return fieldClass.isArray();    }}